export const searchProducts = (query: string): ProductItem[] => {
  if (!query || query.length < 2) return [];
  const lowerQuery = query.toLowerCase();
  return productCatalog.filter(
    (product) =>
      product.sku.toLowerCase().includes(lowerQuery) ||
      product.description.toLowerCase().includes(lowerQuery)
  );
};

export const getProductBySku = (sku: string): ProductItem | undefined => {
  return productCatalog.find((product) => product.sku === sku);
};

export const getProductPrice = (sku: string, priceList: PriceList): number | null => {
  const product = getProductBySku(sku);
  if (!product) return null;
  return product.prices[priceList];
};
